import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax
class Map extends React.Component {
    constructor(props) {
      super(props);
      this.mapContainer = React.createRef();
    }
  
    componentDidMount() {
      mapboxgl.accessToken = 'pk.eyJ1IjoibWVobW9vZDk1MDEiLCJhIjoiY2xoZWl3Z2wyMHpyeTNncnBuaXFkdHFvNCJ9.Mr6A8qVIFGTrM3f8cVIH7A';
      this.map = new mapboxgl.Map({
        container: this.mapContainer.current,
        style: 'mapbox://styles/mapbox/streets-v12',
        center: [0, 0],
        zoom: 2
      });
  
      this.map.on('load', () => {
        this.map.addSource('african-countries', {
          type: 'geojson',
          data: 'use-mapbox-gl-js-with-react/GeoJsons/africa-outline-with-countries_6.geojson'
        });
  
        this.map.addLayer({
          id: 'african-countries-layer',
          type: 'fill',
          source: 'african-countries',
          paint: {
            'fill-color': '#0080ff',
            'fill-opacity': 0.5
          }
        });
  
        this.map.on('click', 'african-countries-layer', (e) => {
          console.log('Clicked on feature:', e.features[0]);
          var countryName = e.features[0].properties.name;
          alert(countryName);

        });
      });
    }
  
    render() {
      return (
        <div
          ref={this.mapContainer}
          style={{ width: '100%', height: '500px' }}
        />
      );
    }
  }
export default Map;
