import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax
//import Map from './Map'
mapboxgl.accessToken = 'pk.eyJ1IjoibWVobW9vZDk1MDEiLCJhIjoiY2xoZWl3Z2wyMHpyeTNncnBuaXFkdHFvNCJ9.Mr6A8qVIFGTrM3f8cVIH7A';

export default function App() {
    const [latitude, setLatitude] = useState('');
    useEffect(() => {
        const map = new mapboxgl.Map({
          container: 'map',
          style: 'mapbox://styles/mapbox/streets-v12',
          center: [17.5, 4.5], // Centered on Africa
          zoom: 3 // Zoomed out to show Africa
        });
        map.on('load', function() {
          map.addSource('african-countries', {
            type: 'geojson',
            data: 'use-mapbox-gl-js-with-react/GeoJsons/africa-outline-with-countries_6.geojson'
          });
        
          // Add a layer to display the countries
          map.addLayer({
            id: 'african-countries-layer',
            type: 'fill',
            source: 'african-countries',
            paint: {
              'fill-color': '#0080ff',
              'fill-opacity': 0.5
            }
          });
        });
        map.on('click', function(e) {
          var features = map.queryRenderedFeatures(e.point, {
            layers: ['african-countries-layer']
          });
        
          if (!features.length) {
            return;
          }
        
          var feature = features[0];
          var countryName = feature.properties.name;
          
          // Do something with the country name, e.g. show it in an alert dialog
          alert(countryName);
        });
      });
      return (
        <div>
          <div id="map" className="map-container" />
          {/* {latitude && <p>Latitude: {latitude}</p>} */}
        </div>
      );
  // return(
  //   <div>
  //     <h1>African Countries Map</h1>
  //     <Map />
  //   </div>
  // )
}
