    //         layers: ['african-countries-layer']
    //       });
        
    //       if (!features.length) {
    //         return;
    //       }
        
    //       var feature = features[0];
    //       var countryName = feature.properties.name;
          
    //       // Do something with the country name, e.g. show it in an alert dialog
    //       alert(countryName);
    //     });
    class Map extends React.Component {
      constructor(props) {
        super(props);
        this.mapContainer = React.createRef();
      }
    
      componentDidMount() {
        mapboxgl.accessToken = 'YOUR_ACCESS_TOKEN';
        this.map = new mapboxgl.Map({
          container: this.mapContainer.current,
          style: 'YOUR_MAP_STYLE_URL',
          center: [0, 0],
          zoom: 2
        });
    
        this.map.on('load', () => {
          this.map.addSource('african-countries', {
            type: 'geojson',
            data: '/path/to/your/local/geojson/file'
          });
    
          this.map.addLayer({
            id: 'african-countries-layer',
            type: 'fill',
            source: 'african-countries',
            paint: {
              'fill-color': '#0080ff',
              'fill-opacity': 0.5
            }
          });
    
          this.map.on('click', 'african-countries-layer', (e) => {
            var countryName = e.features[0].properties.name;
            alert(countryName);
          });
        });
      }
    
      render() {
        return (
          <div
            ref={this.mapContainer}
            style={{ width: '100%', height: '500px' }}
          />
        );
      }
    }
    
